document.querySelectorAll(".checkout__blik-input, .checkout__card-input").forEach((t=>{t.addEventListener("change",(function(){const t=this.getAttribute("data-description"),e=document.getElementById(t);document.querySelectorAll(".checkout__payment-description").forEach((t=>{t.style.height="0",t.style.opacity="0"})),e&&(e.style.height="calc(var(--index) * 1)",e.style.opacity="1")}))}));